// SPDX-License-Identifier: SimPL-2.0

pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/**·
* @title EthRandom - Get a verifiable random number with Chainlink VRF
*
* Docs: https://docs.chain.link/docs/get-a-random-number/
* Contract Address & Fee: https://docs.chain.link/docs/vrf-contracts/
* Polygon: https://blog.chain.link/how-to-get-a-random-number-on-polygon/
*
* Remark: After the contract is deployed, it must be ensured that there are more
* than 5 random numbers in the random number pool
*
* Version v1.0.0
*
*/

abstract contract EthRandom is VRFConsumerBase {
    using EnumerableSet for EnumerableSet.UintSet;

    address constant internal VRF_COORDINATOR = 0x8C7382F9D8f56b33781fE506E897a4F1e2d17255;
    address constant internal LINK_TOKEN = 0x6168499c0cFfCaCD319c818142124B7A15E857ab;

    uint256 constant internal RANDOM_FEE = 0.25 * 10 ** 18;
    bytes32 constant internal KEY_HASH = 0xd89b2bf150e3b9e13446986e571fb9cab24b13cea0a43ea20a6049a85cc807cc;

    EnumerableSet.UintSet private _randomness;
    mapping (bytes32 => uint256) private _requestIds;

    constructor() VRFConsumerBase(VRF_COORDINATOR, LINK_TOKEN) {

    }

    function requestRandom() internal returns (bytes32 requestId) {
        uint256 balance = LINK.balanceOf(address(this));
        require(balance >= RANDOM_FEE, "LINK_NOT_ENOUGH");

        return requestRandomness(KEY_HASH, RANDOM_FEE);
    }

    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
        _requestIds[requestId] = randomness;
        _randomness.add(randomness);
    }

    function getRandomNumber() internal returns (uint256 random) {
        random = uint256(keccak256(abi.encodePacked(
            block.number, block.timestamp
        )));

        _randomness.add(random);
        // if (_randomness.length() > 0) {
        //     random = _randomness.at(0);
        //     _randomness.remove(random);
        // } else {
        //     requestRandom();
        //     random = uint256(keccak256(abi.encodePacked(
        //         block.number, block.timestamp
        //     )));
        // }
    }

    function getRandomBySeed(uint256 randomness, uint256 max) internal pure returns (uint256) {
        bytes memory seed = abi.encodePacked(randomness);
        return uint256(keccak256(seed)) % max;
    }
}
